//SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Registration.sol";

contract Escrow{

    address public registrationContractAddress;
    address public usdcAddress;

    // Events:
    event investedByFundProvider(address indexed _fundProvider,uint indexed _tokens, uint indexed _success);    

    string private l = "low";
    string private m = "medium";
    string private h = "high";

    uint public interestForLowRisk = 2;
    uint public interestForMediumRisk = 5;
    uint public interestForHighRisk = 10;
    uint private identificationNumber;
    uint constant private Status_Success = 200;



    constructor(address _registrationContract, address _usdcAddress) {
        registrationContractAddress = _registrationContract;
        usdcAddress = _usdcAddress;
        identificationNumber = 100;
        interestGenerator[l] = 5;
        interestGenerator[m] = 10;
        interestGenerator[h] = 20;
    }

     // STRUCT
    struct LoanProviderRecord {
        address _loanProviderAddress;
        uint _identificationNumber;
        uint _amountInvested;
        uint _interestPercentage;
        uint _collectedTax;
        uint _actualAmountInvested;
        string _selectedBond;
        uint _speculatingInterestAndPrincipal;
    }

    // MAPPINGS:
    mapping(string => uint) private interestGenerator;
    mapping(address => mapping(uint => bool)) private isIdForInvestor;
    mapping(address => mapping(uint => LoanProviderRecord)) private loanProviderDetails;
    mapping(address => uint[]) private allIdentificationAssociatedWithLoanProviderAddress;

    // Write Functions:
    /**
        * @notice - FUNCTION NAME : depositFundByLoanProvider
    */
    function depositByFundProvider(uint _amount, string memory _bondType) external {
        // registration verification:
        Registration registration = Registration(registrationContractAddress);
        require(registration.isLoanProviderRegistered(msg.sender), "The loan provider address is not registered yet");
        // identification number allocation:
        identificationNumber = identificationNumber + 1;
        isIdForInvestor[msg.sender][identificationNumber] = true;        
        // allocate the details to struct:
        loanProviderDetails[msg.sender][identificationNumber]._loanProviderAddress = msg.sender;
        loanProviderDetails[msg.sender][identificationNumber]._identificationNumber = identificationNumber;
        loanProviderDetails[msg.sender][identificationNumber]._amountInvested = _amount * (10 ** 18);
        loanProviderDetails[msg.sender][identificationNumber]._interestPercentage = interestGenerator[_bondType];
        loanProviderDetails[msg.sender][identificationNumber]._collectedTax = (loanProviderDetails[msg.sender][identificationNumber]._amountInvested * 1) / 100;
        loanProviderDetails[msg.sender][identificationNumber]._selectedBond = _bondType;
        loanProviderDetails[msg.sender][identificationNumber]._actualAmountInvested = loanProviderDetails[msg.sender][identificationNumber]._amountInvested - loanProviderDetails[msg.sender][identificationNumber]._collectedTax;
        uint calculatedInterest = (loanProviderDetails[msg.sender][identificationNumber]._actualAmountInvested * loanProviderDetails[msg.sender][identificationNumber]._interestPercentage) / 100;
        loanProviderDetails[msg.sender][identificationNumber]._speculatingInterestAndPrincipal = loanProviderDetails[msg.sender][identificationNumber]._actualAmountInvested + calculatedInterest;

        // store ids:
        allIdentificationAssociatedWithLoanProviderAddress[msg.sender].push(identificationNumber);
        // get tokens:
        require(ERC20(usdcAddress).transferFrom(msg.sender, address(this), _amount), "transaction failed or reverted");
        emit investedByFundProvider(msg.sender, _amount, Status_Success);
    }

    // /**
    //     * whitelistToken.  
    //     * Whitelist the token address, so that only tokensfrom the whitelist works.
    //     * @param _tokenContract Enter the token contract address to be logged to the smart contract.
    // */
    // function whitelistToken(address _tokenContract) external {
    //     if(_tokenContract == address(0)){ revert zeroAddress();}
    //     if(addressExist[_tokenContract] == true){ revert tokenAlreadyExist();}
    //     addressExist[_tokenContract] = true;
    //     tokenContractAddress.push(_tokenContract);
    // }
}

