// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract mock is Ownable {
    IERC20 public erc20Token;

    event TokensDeposited(address indexed depositor, uint256 amount);
    event TokensWithdrawn(address indexed withdrawer, uint256 amount);

    constructor(address _erc20Token) {
        erc20Token = IERC20(_erc20Token);
    }

    function depositTokens(uint256 _amount) external {
        require(_amount > 0, "Amount must be greater than 0");

        // Transfer tokens from the sender to the contract
        require(erc20Token.transferFrom(msg.sender, address(this), _amount), "Token transfer failed");

        emit TokensDeposited(msg.sender, _amount);
    }

    function withdrawTokens(uint256 _amount) external onlyOwner {
        require(_amount > 0, "Amount must be greater than 0");

        // Ensure the contract has enough balance
        require(erc20Token.balanceOf(address(this)) >= _amount, "Insufficient balance");

        // Transfer tokens from the contract to the owner (only owner can withdraw)
        require(erc20Token.transfer(owner(), _amount), "Token transfer failed");

        emit TokensWithdrawn(owner(), _amount);
    }

    function getContractBalance() external view returns (uint256) {
        return erc20Token.balanceOf(address(this));
    }
}

